import React, { useState } from 'react';
import '../Styles/Toptwobuttons.css';

export default function Toptwobuttons() {
  const [message, setMessage] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [isChatbot, setIsChatbot] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);

  const handleButtonClick = (msg, chatbot = false) => {
    setMessage(msg);
    setIsChatbot(chatbot);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setIsChatbot(false);
    setChatMessages([]);
  };

  const handleChatSubmit = (e) => {
    e.preventDefault();
    const userMessage = e.target.chatInput.value;
    if (userMessage.trim()) {
      setChatMessages((prev) => [
        ...prev,
        { sender: 'user', text: userMessage },
        { sender: 'bot', text: "Thanks! We'll process your request shortly." }, // Simple response
      ]);
    }
    e.target.reset();
  };

  return (
    <div className="container">
      {/* Buttons */}
      <div className="button-container">
        <button
          className="button1"
          onClick={() => window.location.href = 'tel:01132144578'}
        >
          Emergency
        </button>
        <button
          className="button2"
          onClick={() => handleButtonClick('Chat with us about your appointment', true)}
        >
          Appointment
        </button>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            {isChatbot ? (
              <div>
                <h2>Chat with us</h2>
                <div className="chat-window">
                  {chatMessages.map((msg, idx) => (
                    <div
                      key={idx}
                      className={`chat-message ${msg.sender === 'bot' ? 'bot' : 'user'}`}
                    >
                      {msg.text}
                    </div>
                  ))}
                </div>
                <form onSubmit={handleChatSubmit} className="chat-form">
                  <input
                    type="text"
                    name="chatInput"
                    placeholder="Type your message..."
                    required
                  />
                  <button type="submit" className="send-button">
                    Send
                  </button>
                </form>
                <button className="close-button" onClick={closeModal}>
                  Close
                </button>
              </div>
            ) : (
              <div>
                <p>{message}</p>
                <button className="close-button" onClick={closeModal}>
                  Close
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
