import React, { useState } from 'react';
import '../Styles/Mainpg.css';

export default function Main() {
  const [showLoginPopup, setShowLoginPopup] = useState(false);
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [successMessage, setSuccessMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleLogin = () => {
    if (!formData.username || !formData.password) {
      setSuccessMessage('Please fill in both fields!');
    } else {
      setSuccessMessage('Login successful!');
    }
  };

  return (
    <div className="Main-section">
      {/* Logo in the top-left */}
      <div className="logo-container">
        <img
          src={require('../Asset/largpng.png')}
          alt="Logo"
          className="logo-image"
        />
      </div>

      {/* Background and Main Content */}
      <div className="background-container">
        {/* Bottom Left and Bottom Right Colors */}
        <div className="bottom-left-color" />
        <div className="bottom-right-color" />

        {/* Images at the bottom */}
        <div className="images-container">
          <img
            src={require('../Asset/world.png')}
            alt="World Map"
            className="world-image"
          />
          <img
            src={require('../Asset/mainpg.png')}
            alt="Main Illustration"
            className="mainpg-image"
          />
        </div>

        {/* Buttons in the center */}
        <div className="buttons-container">
          <button
            className="square-button"
            onClick={() => setShowLoginPopup(true)}
          >
            Login
          </button>
          <button
            className="square-button"
            onClick={() => alert('Signup functionality to be implemented!')}
          >
            Signup
          </button>
        </div>
      </div>

      {/* Login Popup */}
      {showLoginPopup && (
        <div className="popup-container">
          <div className="popup">
            <h2>Login</h2>
            <input
              type="text"
              name="username"
              placeholder="Enter Username"
              value={formData.username}
              onChange={handleInputChange}
            />
            <input
              type="password"
              name="password"
              placeholder="Enter Password"
              value={formData.password}
              onChange={handleInputChange}
            />
            <button className="popup-button" onClick={handleLogin}>
              Submit
            </button>
            <button
              className="popup-close"
              onClick={() => setShowLoginPopup(false)}
            >
              Close
            </button>
            {successMessage && <p className="success-message">{successMessage}</p>}
          </div>
        </div>
      )}
    </div>
  );
}
