import React from 'react';
import { Link } from 'react-router-dom';
import { TopicBox } from '../components/TopicBox';
import './Home.css';

export default function Home() {	
  return (
    <div style={styles.container}>
      

      {/* Buttons */}
      <div style={styles.buttonContainer}>
        <button style={styles.button} onClick={() => alert('Emergency 011-32144578')}>Emergency</button>
        <button style={styles.button} onClick={() => alert('Make an appointment')}>Appointment</button>
      </div>

      {/* Heading */}
      <h1 style={styles.heading}>Smart Care</h1>

      {/* Navigation Links */}
      <nav style={styles.nav}>
        <Link to="/Aboutus" style={styles.link}>About Us</Link>
        <Link to="/Contactus" style={styles.link}>Contact Us</Link>
        <Link to="/Careers" style={styles.link}>Careers</Link>
        <Link to="/Home" style={styles.link}>Home</Link>
      </nav>

      {/* TopicBox */}
      <div style={styles.topicBoxContainer}>
        <TopicBox />
      </div>
    </div>
  );
}

// Inline styles for simplicity
const styles = {
  container: {
    padding: '20px',
    textAlign: 'center',
  },
  heading: {
    fontSize: '2em',
    color: '#333',
    marginBottom: '20px',
  },
  nav: {
    marginBottom: '30px',
  },
  link: {
    margin: '0 10px',
    textDecoration: 'none',
    color: '#000000',
    fontWeight: 'bold',
  },
  buttonContainer: {
    margin: '20px 0',
  },
  button: {
    margin: '0 10px',
    padding: '10px 20px',
    fontSize: '16px',
    borderRadius: '5px',
    border: 'none',
    backgroundColor: '#007BFF',
    color: 'white',
    cursor: 'pointer',
  },
  topicBoxContainer: {
    marginTop: '30px',
  },
};
