import React, { useState } from 'react';
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';
import '../Styles/Mainbutt.css';

export default function Main() {
  const [formType, setFormType] = useState(null); // Tracks the active form

  return (
    <div>
      {/* Render buttons or forms dynamically */}
      {formType === null && (
        <div className="buttons-container">
          <button
            className="square-button"
            onClick={() => setFormType('login')}
          >
            Login
          </button>
          <button
            className="square-button"
            onClick={() => setFormType('signup')}
          >
            Signup
          </button>
        </div>
      )}

      {formType === 'login' && <LoginForm goBack={() => setFormType(null)} />}
      {formType === 'signup' && <SignupForm goBack={() => setFormType(null)} />}
    </div>
  );
}
