import React, { useState } from 'react';
import '../Styles/Mainpg.css';

export default function Main() {
  const [bgColor, setBgColor] = useState('#ffffff'); // Default background color

  const handleColorChange = (event) => {
    setBgColor(event.target.value);
  };

  return (
    <div className="Main-section" style={{ backgroundColor: bgColor }}>
      {/* Logo */}
      <div className="logo-container">
        <img src="/path/to/logo.png" alt="Logo" className="logo-image" />
      </div>

      {/* Background Image */}
      <img src="/path/to/background-image.jpg" alt="Background" className="background-image" />

      {/* Buttons */}
      <div className="buttons-container">
        <button className="square-button" onClick={() => alert('Login button clicked!')}>
          Login
        </button>
        <button className="square-button" onClick={() => alert('Signup button clicked!')}>
          Signup
        </button>
      </div>

      {/* Background Color Selector */}
      <div className="color-selector">
        <label htmlFor="bgColor">Choose Background Color: </label>
        <input
          type="color"
          id="bgColor"
          value={bgColor}
          onChange={handleColorChange}
        />
      </div>
    </div>
  );
}
